    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String correo = txtCorreo.getText(); 
    String contrase침a = txtcontra.getText(); 
    if (correo.isEmpty() || contrase침a.isEmpty()) {
        javax.swing.JOptionPane.showMessageDialog(this, "Completa todos los campos.");
    } else if (!correo.matches("^[\\w-\\.]+@([\\w-]+\\.)+[\\w-]{2,4}$")) {
        javax.swing.JOptionPane.showMessageDialog(this, "Correo inv치lido.");
    } else {
        javax.swing.JOptionPane.showMessageDialog(this, "Registro exitoso");
        PantallaPrincipal nueva = new PantallaPrincipal(); 
        nueva.setVisible(true);
        this.dispose(); 
    }
    }//GEN-LAST:event_jButton1ActionPerformed

